$sessionId = $proxy->login((object)array('username' => $apiUser, 'apiKey' => $apiKey));

foreach ($images as $image) {
    $result = $proxy->catalogProductAttributeMediaCreate((object)array('sessionId' => $sessionId->result, 'productId' => $productId, 'data' => ((object)array(
        'label' => $image['name'],
        'position' => '1',
        'types' => array('thumbnail'),
        'exclude' => '0',
        'file' => ((object)$image
    )))));
}

file_put_contents('request-wsi.xml', $proxy->__getLastRequest());
file_put_contents('response-wsi.xml', $proxy->__getLastResponse());

exit('END Request Example SOAP V2 (WS-I Compliance Mode)');
/* END Request Example SOAP V2 (WS-I Compliance Mode) */</code></pre>

