<?php

class Mage_Catalog_Model_Layer_Filter_Foggy extends Mage_Catalog_Model_Layer_Filter_Abstract
{
    public function __construct()
    {
        parent::__construct();
        $this->_requestVar = 'foggy';
    }

    public function getName()
    {
        return 'Foggy';
    }    

    protected function _getItemsData()
    {

        $itemsData = array();

        $itemsData[] = array(
            'label' => 'Yeap, foggy it is',
            'value' => 'yes',
            'count' => 23
        );

        $itemsData[] = array(
            'label' => 'Nah, it is not',
            'value' => 'nah',
            'count' => 13
        );        

        return $itemsData;
    } 

    public function apply(Zend_Controller_Request_Abstract $request, $filterBlock)
    {
        $filter = $request->getParam($this->getRequestVar());

        if (!$filter) {
            return $this;
        }



        $this->getLayer()->getState()->addFilter(
            $this->_createItem('WTF is happening :/', $filter)
        );


        return $this;
    }
}


